-- -----------------------------------------------------
-- Table `NYT_Best_Sellers`.`Title`
-- -----------------------------------------------------
CREATE TABLE IF NOT EXISTS `NYT_Best_Sellers`.`Title` (
  `title_id` INT NOT NULL AUTO_INCREMENT,
  `title` VARCHAR(155) NOT NULL,
  PRIMARY KEY (`title_id`),
  UNIQUE INDEX `title_UNIQUE` (`title` ASC) VISIBLE)
ENGINE = InnoDB;


-- -----------------------------------------------------
-- Table `NYT_Best_Sellers`.`Author`
-- -----------------------------------------------------
CREATE TABLE IF NOT EXISTS `NYT_Best_Sellers`.`Author` (
  `author_id` INT NOT NULL AUTO_INCREMENT,
  `author_name` VARCHAR(155) NOT NULL,
  PRIMARY KEY (`author_id`),
  UNIQUE INDEX `author_name_UNIQUE` (`author_name` ASC) VISIBLE)
ENGINE = InnoDB;


-- -----------------------------------------------------
-- Table `NYT_Best_Sellers`.`Genres`
-- -----------------------------------------------------
CREATE TABLE IF NOT EXISTS `NYT_Best_Sellers`.`Genres` (
  `genre_id` INT NOT NULL AUTO_INCREMENT,
  `genre` VARCHAR(155) NOT NULL,
  PRIMARY KEY (`genre_id`),
  UNIQUE INDEX `genre_UNIQUE` (`genre` ASC) VISIBLE)
ENGINE = InnoDB;


-- -----------------------------------------------------
-- Table `NYT_Best_Sellers`.`Price`
-- -----------------------------------------------------
CREATE TABLE IF NOT EXISTS `NYT_Best_Sellers`.`Price` (
  `price_id` INT NOT NULL AUTO_INCREMENT,
  `Amazon_price` DOUBLE NOT NULL,
  `Apple_Books_price` DOUBLE NOT NULL,
  PRIMARY KEY (`price_id`))
ENGINE = InnoDB;


-- -----------------------------------------------------
-- Table `NYT_Best_Sellers`.`Rating`
-- -----------------------------------------------------
CREATE TABLE IF NOT EXISTS `NYT_Best_Sellers`.`Rating` (
  `rating_id` INT NOT NULL AUTO_INCREMENT,
  `Amazon_rating` DOUBLE NOT NULL,
  `Apple_books_rating` DOUBLE NOT NULL,
  `Amazon_rating_count` DOUBLE NOT NULL,
  `Apple_rating_count` DOUBLE NOT NULL,
  PRIMARY KEY (`rating_id`))
ENGINE = InnoDB;


-- -----------------------------------------------------
-- Table `NYT_Best_Sellers`.`Publisher`
-- -----------------------------------------------------
CREATE TABLE IF NOT EXISTS `NYT_Best_Sellers`.`Publisher` (
  `publisher_id` INT NOT NULL AUTO_INCREMENT,
  `publishing_company` VARCHAR(150) NOT NULL,
  PRIMARY KEY (`publisher_id`),
  UNIQUE INDEX `publishing_company_UNIQUE` (`publishing_company` ASC) VISIBLE)
